mapper.select:
SELECT id, uid, content, title, publish, cdt FROM ii_blogs WHERE

    id = #{id}

        uid = #{username}

SELECT id, uid, content, title, publish, cdt
FROM ii_blogs
WHERE uid = #{uid}

SELECT id, uid, content, title, publish, cdt FROM ii_blogs

    uid = #{entity.uid}

ORDER BY id DESC
LIMIT #{page.limit} OFFSET #{page.offset}

mapper.update:
UPDATE ii_blogs

    title = #{title}
    ,content = #{content}
    ,category_id = #{categoryId}

WHERE id = #{id} AND uid = #{uid}

UPDATE ii_blogs

    publish = !publish, version = version + 1

WHERE id = #{id} AND uid = #{uid}

mapper.select:
SELECT COUNT(1)
FROM ii_blogs
WHERE id = #{id}
